// SPDX-License-Identifier: MIT
pragma solidity 0.6.6;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract DrexTreasury {
    //varibales required in smart contract
    IERC20 private drex_stable_coin;
    mapping(bytes32 => address) public reqTosender;
    uint256 random_amount;

    // events
    event TransferSent(address _destAddr, uint256 _amount);

    constructor(IERC20 stable_coin_address) public {
        drex_stable_coin = stable_coin_address;
    }

    function send_stable_coins(uint256 amount) public payable {
        require(
            drex_stable_coin.balanceOf(msg.sender) > amount,
            "Balance not enough,please recharge"
        );

        drex_stable_coin.transferFrom(msg.sender, address(this), amount);
        emit TransferSent(address(this), random_amount);
    }

    function redemption(uint256 amount) public {
        require(
            drex_stable_coin.balanceOf(address(this)) >= amount,
            "amount greater than contract balance"
        );
        drex_stable_coin.transfer(msg.sender, amount);
    }
}
