// SPDX-License-Identifier: MIT
pragma solidity 0.6.6;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@chainlink/contracts/src/v0.6/VRFConsumerBase.sol";

contract DrexTreasury is VRFConsumerBase {
    //variables for chainlinkVrf
    bytes32 internal keyhash;
    uint256 fee;

    //varibales required in smart contract
    IERC20 private drex_stable_coin;
    mapping(bytes32 => address) public reqTosender;
    uint256 random_amount;

    // events
    event TransferSent(address _destAddr, uint256 _amount);

    constructor(
        address _VRFCoordinator,
        address _LinkToken,
        bytes32 _keyhash,
        IERC20 stable_coin_address
    ) public VRFConsumerBase(_VRFCoordinator, _LinkToken) {
        drex_stable_coin = stable_coin_address;
        keyhash = _keyhash;
        fee = 0.1 * 10 * 18;
    }

    function DrexToStable(uint256 amount) public returns (bytes32) {
        bytes32 requestId = requestRandomness(keyhash, fee);
        reqTosender[requestId] = msg.sender;
    }

    function fulfillRandomness(bytes32 requestId, uint256 randomNumber)
        internal
        override
    {
        random_amount = (randomNumber % 100) + 1;
    }

    function send_stable_coins() public payable {
        require(
            drex_stable_coin.balanceOf(msg.sender) > random_amount,
            "Balance not enough,please recharge"
        );

        drex_stable_coin.transferFrom(msg.sender, address(this), random_amount);
        emit TransferSent(address(this), random_amount);
    }

    function redemption(uint256 amount) public {
        require(
            drex_stable_coin.balanceOf(address(this)) >= amount,
            "amount greater than contract balance"
        );
        drex_stable_coin.transfer(msg.sender, amount);
    }
}
